import memesData from "../memesData";
import React from 'react';


export default function Meme() {


    const [meme,setMeme] = React.useState({
        topText: '',
        bottomText: '',
        randomImage: 'http://i.imgflip.com/1bij.jpg'
    })

    const [allMemeImage,setAllMemeImages] = React.useState(memesData)
   console.log(allMemeImage) // object





    function getMemeImage()  {

       const memesArray = allMemeImage.data.memes // array

        const randomNumber =  Math.floor(Math.random() * memesArray.length)

        console.log(randomNumber)  // random array number

        const url = memesArray[randomNumber].url

        console.log(url)  // image properties

        setMeme(prestate =>({
            ...prestate,
               randomImage : url
        }))


    }
    function handleChange(event) {
       const {name,value} = event.target
        setMeme(prevMeme => ({
            ...prevMeme,
            [name] :  value
        }))
    }
    return (
        <main >
            <div className="form">
                <input
                    type="text"
                    placeholder="Top text"
                    className="form--input"
                    name= "topText"
                    value={meme.topText}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    placeholder="Bottom text"
                    className="form--input"
                    name="bottomText"
                    value={meme.bottomText}
                    onChange={handleChange}

                />
                <button
                    className="form--button"
                    onClick={getMemeImage}
                >
                    Get a new meme image ðŸ–¼
                </button>
            </div>
            <div className="meme">
                <img src={meme.randomImage} className="meme--image" />
                <h2 className="meme--text top">{meme.topText}</h2>
                <h2 className="meme--text bottom">{meme.bottomText}</h2>
            </div>
        </main>
)

}
